import React from "react";

// import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
var _ = require("lodash");

export default function Robot(props) {
  const { id, robot, cartItems } = props;
  const all_robots = useSelector((state) => state.products.robots);

  function addToCart(item) {
    let cartItem = _.findIndex(all_robots, function (o) {
      return o.name === item;
    });
    // console.log(all_robots);
  }

  return (
    <div className="card col-md-3 m-4" id={id}>
      <figure>
        <img
          className="card-img"
          src={robot.image}
          alt={robot.name + "-image"}
          style={{ width: 120 }}
        />
        <span className="card-category">{robot.material}</span>
        <span className="price">
          <b>$</b>
          {robot.price}
        </span>
        <span className="card-item-in-stock">
          <b>In stock: </b>
          {robot.stock}
        </span>
      </figure>
      <div className="card-info d-flex align-items-center justify-content-between">
        <div className="left">
          <p className="card-item-name">{robot.name}</p>
          <span className="created-at">{robot.createdAt}</span>
        </div>
        <div className="right">
          <button
            className="btn btn-primary"
            onClick={() => addToCart(robot.name)}
          >
            {/* <img
              src={CartIconPlus}
              height="20px"
              width="20px"
              alt="add-to-cart"
            /> */}
            add
          </button>
        </div>
      </div>
    </div>
  );
}
