import React from "react";

import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import * as moment from "moment";

import { addToCart, removeFromCart } from "../../features/CartReducer";
var _ = require("lodash");

export default function Robot(props) {
  const dispatch = useDispatch();
  const { id, robot } = props;
  const all_robots = useSelector((state) => state.products.robots);
  const subscribedRobots = useSelector((state) => state.cart.cartItems);

  function subscribeCart(item) {
    if (robot.stock !== 0) {
      let cartItem = _.find(all_robots, { name: item });
      // let setSub
      // check to see if item is in cart already
      if (_.find(subscribedRobots, { name: item })) {
      } else {
        // console.log(cartItem);
        dispatch(addToCart(cartItem));
      }
    }
  }
  function unSubscribeCart(item) {
    if (robot.stock !== 0) {
      let cartItem = _.find(all_robots, { name: item });

      dispatch(removeFromCart(cartItem));
    }
  }
  useEffect(() => {
    // addedStatus;
  });
  return (
    <div className="col-md-3" id={id}>
      <div className="card">
        <figure>
          <img src={robot.image} alt={"image-" + robot.name.toLowerCase()} />
          <span className="material">{robot.material}</span>
          <span className="price">{robot.price}</span>
        </figure>
        <div className="card-body">
          <p className="card-title">{robot.name}</p>
          <p className="date">{moment(robot.createdAt).format("DD-MM-YYYY")}</p>
        </div>
      </div>
      <figure>
        <img
          className="card-img"
          src={robot.image}
          alt={robot.name + "-image"}
          style={{ width: 120 }}
        />
        <span className="card-category">{robot.material}</span>
        <span className="price">
          <b>$</b>
          {robot.price}
        </span>
        <span className="card-item-in-stock">
          <b>In stock: </b>
          {robot.stock}
        </span>
      </figure>
      <div className="card-info d-flex align-items-center justify-content-between">
        <div className="left">
          <p className="card-item-name">{robot.name}</p>
          <span className="created-at">{robot.createdAt}</span>
        </div>
        <div className="right">
          <button
            className="btn btn-primary"
            onClick={() =>
              _.find(subscribedRobots, robot)
                ? unSubscribeCart(robot.name)
                : subscribeCart(robot.name)
            }
          >
            {robot.stock > 0
              ? _.find(subscribedRobots, robot)
                ? "remove"
                : "add"
              : "outofstocks"}
          </button>
        </div>
      </div>
    </div>
  );
}
