import React, { useState } from "react";
// import { robotSlice } from "../../features/robots/RobotsReducer";
import { useSelector, useDispatch } from "react-redux";
import {
  removeFromCart,
  incrementCartItem,
  decrementCartItem,
} from "../../features/CartReducer";

export default function CartItem(props) {
  const { item } = props;
  const dispatch = useDispatch();
  const [count, setCount] = useState(1);

  function addCount(item) {
    if (item.stock > count && count < 5) {
      setCount(count + 1);
    } else {
      alert("Max items reached");
    }
  }

  function removeCount(item) {
    if (count !== 1) {
      setCount(count - 1);
    } else {
      dispatch(removeFromCart(item));
    }
  }
  return (
    <div className="cart-item card">
      <p>
        <b>Name :{item.name} </b>
      </p>
      <p>Number: {count}</p>
      <p>
        <button onClick={() => dispatch(incrementCartItem(item))}>+</button>
        &nbsp;&nbsp;
        <button onClick={() => dispatch(decrementCartItem(item))}>-</button>
      </p>
    </div>
  );
}
