import { configureStore } from "@reduxjs/toolkit";
import allrobots from "../features/robots/RobotsReducer";
import cart_box from "../features/CartReducer";

const numberPersistConfig = {
  key: "persistedStore",
  version: 1,
  migrate: createMigrate(migrations, { debug: MIGRATION_DEBUG }),
};

const reducers = combineReducers({
  persistedStore: persist(numberPersistConfig, numberReducer),
  notPersistedStore: messageReducer,
});

const store = createStore(
  reducers,
  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
);

export const persistor = persistStore(store);

export default store;

